import React, { useEffect, useState } from 'react';
import api from '../api';
import { Link } from 'react-router-dom';

function Dashboard() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    api.get('/users').then(res => setUsers(res.data));
  }, []);

  const deleteUser = (id) => {
    if (window.confirm('Are you sure to delete this user?')) {
      api.delete(`/users/${id}`).then(() => setUsers(users.filter(u => u._id !== id)));
    }
  };

  return (
    <div className="container py-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2>User Dashboard</h2>
        <Link to="/user/new" className="btn btn-primary">Add User</Link>
      </div>
      <table className="table table-striped table-bordered table-hover">
        <thead className="table-dark">
          <tr>
            <th>Name</th><th>Email</th><th>Phone</th><th>Company</th><th>City</th><th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map(u => (
            <tr key={u._id}>
              <td>{u.name}</td>
              <td>{u.email}</td>
              <td>{u.phone}</td>
              <td>{u.company}</td>
              <td>{u.address.city}</td>
              <td>
                <Link to={`/user/${u._id}`} className="btn btn-sm btn-info me-1">View</Link>
                <Link to={`/user/${u._id}/edit`} className="btn btn-sm btn-warning me-1">Edit</Link>
                <button onClick={() => deleteUser(u._id)} className="btn btn-sm btn-danger">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Dashboard;
