import React, { useEffect, useState } from 'react';
import api from '../api';
import { Link } from 'react-router-dom';

function Dashboard() {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    api.get('/users').then(res => {
      // Sort users alphabetically by name on fetch
      const sortedUsers = res.data.sort((a, b) =>
        a.name.localeCompare(b.name)
      );
      setUsers(sortedUsers);
    });
  }, []);

  // Filter users by name or email based on searchTerm
  const filteredUsers = users.filter(user =>
    user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    user.email.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const deleteUser = (id) => {
    if (window.confirm('Are you sure to delete this user?')) {
      api.delete(`/users/${id}`).then(() =>
        setUsers(prevUsers => prevUsers.filter(u => u._id !== id))
      );
    }
  };

  return (
    <div className="container py-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2>User Dashboard</h2>
        <Link to="/user/new" className="btn btn-primary">Add User</Link>
      </div>

      {/* Search input */}
      <div className="mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Search by name or email..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Users table */}
      <table className="table table-striped table-bordered table-hover">
        <thead className="table-dark">
          <tr>
            <th>Name</th><th>Email</th><th>Phone</th><th>Company</th><th>City</th><th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredUsers.length > 0 ? (
            filteredUsers.map(u => (
              <tr key={u._id}>
                <td>{u.name}</td>
                <td>{u.email}</td>
                <td>{u.phone}</td>
                <td>{u.company}</td>
                <td>{u.address.city}</td>
                <td>
                  <Link to={`/user/${u._id}`} className="btn btn-sm btn-info me-1">View</Link>
                  <Link to={`/user/${u._id}/edit`} className="btn btn-sm btn-warning me-1">Edit</Link>
                  <button onClick={() => deleteUser(u._id)} className="btn btn-sm btn-danger">Delete</button>
                </td>
              </tr>
            ))
          ) : (
            <tr><td colSpan="6" className="text-center">No matching users found.</td></tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default Dashboard;
