import React, { useState, useEffect } from 'react';
import api from '../api';
import { useParams, useNavigate } from 'react-router-dom';

function EditUser() {
  const { id } = useParams();
  const [form, setForm] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    api.get(`/users/${id}`).then(res => setForm(res.data));
  }, [id]);

  if (!form) return <div className="container py-4">Loading...</div>;

  const handleChange = e => {
    const { name, value } = e.target;
    if (name.startsWith('address.')) {
      const [, field] = name.split('.');
      setForm({ ...form, address: { ...form.address, [field]: value } });
    } else if (name.startsWith('geo.')) {
      const [, field] = name.split('.');
      setForm({ ...form, address: { ...form.address, geo: { ...form.address.geo, [field]: value } } });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (!form.email.match(/.+@.+\..+/)) return setError('Invalid email format');
    try {
      await api.put(`/users/${id}`, form);
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.message || 'Server error');
    }
  };

  return (
    <div className="container py-4">
      <h2>Edit User</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      <form onSubmit={handleSubmit} className="row g-3">
        <div className="col-md-6">
          <label className="form-label">Name</label>
          <input name="name" className="form-control" required value={form.name} onChange={handleChange} />
        </div>
        <div className="col-md-6">
          <label className="form-label">Email</label>
          <input name="email" type="email" className="form-control" required value={form.email} onChange={handleChange} />
        </div>
        <div className="col-md-6">
          <label className="form-label">Phone</label>
          <input name="phone" className="form-control" required value={form.phone} onChange={handleChange} />
        </div>
        <div className="col-md-6">
          <label className="form-label">Company</label>
          <input name="company" className="form-control" required value={form.company} onChange={handleChange} />
        </div>

        <div className="col-12">
          <h5>Address</h5>
        </div>
        <div className="col-md-6">
          <label className="form-label">Street</label>
          <input name="address.street" className="form-control" required value={form.address.street} onChange={handleChange} />
        </div>
        <div className="col-md-4">
          <label className="form-label">City</label>
          <input name="address.city" className="form-control" required value={form.address.city} onChange={handleChange} />
        </div>
        <div className="col-md-2">
          <label className="form-label">Zipcode</label>
          <input name="address.zipcode" className="form-control" required value={form.address.zipcode} onChange={handleChange} />
        </div>

        <div className="col-12">
          <h5>Geo Location</h5>
        </div>
        <div className="col-md-6">
          <label className="form-label">Latitude</label>
          <input name="geo.lat" className="form-control" required value={form.address.geo.lat} onChange={handleChange} />
        </div>
        <div className="col-md-6">
          <label className="form-label">Longitude</label>
          <input name="geo.lng" className="form-control" required value={form.address.geo.lng} onChange={handleChange} />
        </div>

        <div className="col-12">
          <button type="submit" className="btn btn-success mt-3">Save Changes</button>
        </div>
      </form>
    </div>
  );
}

export default EditUser;
