import React, { useEffect, useState } from 'react';
import api from '../api';
import { Link } from 'react-router-dom';

function Dashboard() {
  const [users, setUsers] = useState([]);
  useEffect(() => {
    api.get('/user').then(res => setUsers(res.data));
  }, []);

  const deleteUser = (id) => {
    if(window.confirm('Delete this user?')) {
      api.delete(`/users/${id}`)
        .then(() => setUsers(users.filter(u => u._id !== id)));
    }
  };

  return (
    <div>
      <h2>User Dashboard</h2>
      <Link to="/user/new">Add User</Link>
      <table>
        <thead>
          <tr>
            <th>Name</th><th>Email</th><th>Phone</th><th>Company</th><th>City</th><th>Actions</th>
          </tr>
        </thead>
        <tbody>
        {users.map(u => (
          <tr key={u._id}>
            <td>{u.name}</td>
            <td>{u.email}</td>
            <td>{u.phone}</td>
            <td>{u.company}</td>
            <td>{u.address.city}</td>
            <td>
              <Link to={`/user/${u._id}`}>View</Link>
              <Link to={`/user/${u._id}/edit`}>Edit</Link>
              <button onClick={() => deleteUser(u._id)}>Delete</button>
            </td>
          </tr>
        ))}
        </tbody>
      </table>
    </div>
  );
}
export default Dashboard;
