import React, { useState, useEffect } from 'react';
import api from '../api';
import { useParams, useNavigate } from 'react-router-dom';

function EditUser() {
  const { id } = useParams();
  const [form, setForm] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    api.get(`/users/${id}`).then(res => setForm(res.data));
  }, [id]);

  if (!form) return <div>Loading...</div>;

  const handleChange = e => {
    const { name, value } = e.target;
    if(name.startsWith('address.')) {
      const [_, field] = name.split('.');
      setForm({ ...form, address: { ...form.address, [field]: value } });
    } else if(name.startsWith('geo.')) {
      const [_, field] = name.split('.');
      setForm({ ...form, address: { ...form.address, geo: { ...form.address.geo, [field]: value } } });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if(!form.email.match(/.+@.+\..+/)) return setError('Invalid email.');
    try {
      await api.put(`/users/${id}`, form);
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.message || 'Server error');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Edit User</h2>
      {error && <div style={{color:'red'}}>{error}</div>}
      <input name="name" required value={form.name} onChange={handleChange}/>
      <input name="email" required value={form.email} onChange={handleChange}/>
      <input name="phone" required value={form.phone} onChange={handleChange}/>
      <input name="company" required value={form.company} onChange={handleChange}/>
      <input name="address.street" required value={form.address.street} onChange={handleChange}/>
      <input name="address.city" required value={form.address.city} onChange={handleChange}/>
      <input name="address.zipcode" required value={form.address.zipcode} onChange={handleChange}/>
      <input name="geo.lat" required value={form.address.geo.lat} onChange={handleChange}/>
      <input name="geo.lng" required value={form.address.geo.lng} onChange={handleChange}/>
      <button type="submit">Save</button>
    </form>
  );
}
export default EditUser;
