import React, { useState } from 'react';
import api from '../api';
import { useNavigate } from 'react-router-dom';

function UserForm() {
  const [form, setForm] = useState({
    name: '', email: '', phone: '', company: '',
    address: { street: '', city: '', zipcode: '', geo: { lat: '', lng: '' } }
  });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = e => {
    const { name, value } = e.target;
    if(name.startsWith('address.')) {
      const [_, field] = name.split('.');
      setForm({ ...form, address: { ...form.address, [field]: value } });
    } else if(name.startsWith('geo.')) {
      const [_, field] = name.split('.');
      setForm({ ...form, address: { ...form.address, geo: { ...form.address.geo, [field]: value } } });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    // Simple client-side validation
    if(!form.email.match(/.+@.+\..+/)) return setError('Invalid email.');
    try {
      await api.post('/users', form);
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.message || 'Server error');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Create New User</h2>
      {error && <div style={{color:'red'}}>{error}</div>}
      <input name="name" placeholder="Name" required value={form.name} onChange={handleChange}/>
      <input name="email" placeholder="Email" required value={form.email} onChange={handleChange}/>
      <input name="phone" placeholder="Phone" required value={form.phone} onChange={handleChange}/>
      <input name="company" placeholder="Company" required value={form.company} onChange={handleChange}/>
      <input name="address.street" placeholder="Street" required value={form.address.street} onChange={handleChange}/>
      <input name="address.city" placeholder="City" required value={form.address.city} onChange={handleChange}/>
      <input name="address.zipcode" placeholder="Zipcode" required value={form.address.zipcode} onChange={handleChange}/>
      <input name="geo.lat" placeholder="Latitude" required value={form.address.geo.lat} onChange={handleChange}/>
      <input name="geo.lng" placeholder="Longitude" required value={form.address.geo.lng} onChange={handleChange}/>
      <button type="submit">Create</button>
    </form>
  );
}
export default UserForm;
